// ... (your imports)
import React, { useState, useRef } from "react";
import "../styles/stylesAnalista.css";


const DragDropFilesV2 = () => {
    const [files, setFiles] = useState(null);
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [showPopup, setShowPopup] = useState(false);
    const inputRef = useRef();
  
    const handleDragOver = (event) => {
      event.preventDefault();
    };
  
    const handleDrop = (event) => {
      event.preventDefault();
      setFiles(event.dataTransfer.files);
      setSelectedFiles(Array.from(event.dataTransfer.files));
    };
  
    const getFiles = () => selectedFiles;
  
    // send files to the server
    const handleUpload = () => {
      const formData = new FormData();
      const filesToUpload = getFiles();
      filesToUpload.forEach((file, index) => {
        formData.append(`file${index + 1}`, file);
      });
  
      // Uncomment the fetch block when you have the server endpoint
      /*
      fetch("link", {
        method: "POST",
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("Upload successful", data);
          setShowPopup(true);
          setFiles(null);
          setSelectedFiles([]);
        })
        .catch((error) => {
          console.error("Error uploading files:", error);
        });
      */
    };
  
    const closePopup = () => {
      setShowPopup(false);
    };
  
    const handleInserir = () => {
      // This is where you use getFiles
      const filesToInsert = getFiles();
      console.log("Files to insert:", filesToInsert);
      // The rest of your logic for inserting files
    };
  
    if (files) {
      return (
        <div className="uploads">
          <ul>
            {Array.from(files).map((file, idx) => (
              <li key={idx}>{file.name}</li>
            ))}
          </ul>
          <div className="actions">
            <button onClick={() => setFiles(null)}>Cancelar</button>
            <button onClick={handleUpload}>Upload</button>
            <button onClick={handleInserir}>Inserir</button>
          </div>
          {showPopup && (
            <div className="popup">
              <p>Imagens inseridas com sucesso</p>
              <button onClick={closePopup}>Fechar</button>
            </div>
          )}
        </div>
      );
    }
  
    return (
      <>
        <div className="dropzone" onDragOver={handleDragOver} onDrop={handleDrop}>
          <h1>Arraste as imagens</h1>
          <h1>Ou</h1>
          <input
            type="file"
            multiple
            onChange={(event) => {
              setFiles(event.target.files);
              setSelectedFiles(Array.from(event.target.files));
            }}
            hidden
            accept="image/png, image/jpeg"
            ref={inputRef}
          />
          <button onClick={() => inputRef.current.click()}>Selecione as imagens</button>
        </div>
      </>
    );
  };
  
  export default DragDropFilesV2;
