import { useState, useRef } from "react";
import { Redirect } from "react-router-dom";
import "../styles/stylesAnalista.css";

const DragDropFilesV2 = () => {
  const [files, setFiles] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [redirect, setRedirect] = useState(false);
  const inputRef = useRef();

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setFiles(event.dataTransfer.files);
  };

  // send files to the server
  const handleUpload = () => {
    const formData = new FormData();
    Array.from(files).forEach((file, index) => {
      formData.append(`file${index + 1}`, file);
    });

    // Uncomment the fetch block when you have the server endpoint
    /*
    fetch("link", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Upload successful", data);
        setShowPopup(true);
        setFiles(null);
        // Redirect to "/telaAnalista" after a successful upload
        setRedirect(true);
      })
      .catch((error) => {
        console.error("Error uploading files:", error);
      });
    */
  };

  const closePopup = () => {
    setShowPopup(false);
  };

  if (redirect) {
    return <Redirect to="/telaAnalista" />;
  }

  if (files) {
    return (
      <div className="uploads">
        <ul>
          {Array.from(files).map((file, idx) => (
            <li key={idx}>{file.name}</li>
          ))}
        </ul>
        <div className="actions">
          <button onClick={() => setFiles(null)}>Cancel</button>
          <button onClick={handleUpload}>Upload</button>
        </div>
        {showPopup && (
          <div className="popup">
            <p>Files uploaded successfully!</p>
            <button onClick={closePopup}>Close</button>
          </div>
        )}
      </div>
    );
  }

  return (
    <>
      <div className="dropzone" onDragOver={handleDragOver} onDrop={handleDrop}>
        <h1>Arraste as imagens</h1>
        <h1>Ou</h1>
        <input
          type="file"
          multiple
          onChange={(event) => setFiles(event.target.files)}
          hidden
          accept="image/png, image/jpeg"
          ref={inputRef}
        />
        <button onClick={() => inputRef.current.click()}>Selecione as imagens</button>
      </div>
    </>
  );
};

export default DragDropFilesV2;
