import React, { useState, useEffect } from "react";
import "../styles.css";
import { Link } from "react-router-dom"; // Importe a funcionalidade de Link para criar links
import axios from "axios";
import EstadosCidades from '../components/estadosCidades';
import SelectFiliais from "../components/selectFiliais";
import DragDrop from "../components/dragDrop";
import DragDropFiles from "../components/dragDropFiles";


function imagemAnalista() {
  // Estado para armazenar os dados do usuário
//   const [userData, setUserData] = useState(null);

  // Efeito para carregar os dados do usuário ao montar o componente
//   useEffect(() => {
    // Simulação de uma solicitação ao backend
//     axios
//       .get("/api/user") // Substitua '/api/user' pela rota correta do seu backend
//       .then((response) => {
//         setUserData(response.nomeUsuario);
//       })
//       .catch((error) => {
//         console.error("Erro ao obter dados do usuário:", error);
//       });
//   }, []);

return (
    
    <div className="container">
      <div className="container-login">
        <div className="wrap-login">
          <form className="login-form" action="http://127.0.0.1:8000/processarCadastroUsuario/" method="post">
            <span className="login-form-title">Inserir Imagem</span>
            <span className="login-form-title">
            </span>

            <div className='wrap-input'>
              <input className='input' name='cidade' type='text' placeholder='Cidade' required/>
            </div>

            <SelectFiliais/>

            <div className='wrap-input'>
              <input className='input' name='password' type='password' placeholder='Password' required/>
            </div>

          <div className='wrap-input'>

         <select className='input' name='tipoUsuario'>
          <option value='normal'>Normal</option>
           <option value='especialista'>Especialista</option>
             <option value='admin'>Admin</option>
           </select>
            </div>

            <DragDropFiles/>

            <div className='container-login-form-btn'>
              <button className='logon-form-btn' type='submit'>
                CADASTRAR
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default imagemAnalista;
